import React from "react";
import "./trustedCompanies.css";

const logos = [
  "data:image/jpeg;base64,",
  "https://th.bing.com/th/id/OIP.Gq2B7AWOMMuhySgklXT8mQHaCy?w=350&h=131&c=7&r=0&o=5&pid=1.7",
  "https://logos-world.net/wp-content/uploads/2020/04/Linkedin-Logo.png",
  "https://th.bing.com/th/id/OIP.fEK3OSxk33pkYR7ErSBISAHaHa?rs=1&pid=ImgDetMain",
  "https://th.bing.com/th/id/OIP.QZ87fvkLF8WJGTH5TAIzRAHaB4?rs=1&pid=ImgDetMain",
  "https://th.bing.com/th/id/OIP.9xE28luefbi0E-t82Xmc7QHaHK?rs=1&pid=ImgDetMain",
];

const TrustedCompanies = () => {
  return (
    <div className="trusted-companies-container">
      <h1 
        style={{
          color: "#363636",
        margin:"5%",
      }} 
                className="text-5xl font-bold text-center" >Top Companies Trust Us</h1>
      <div className="fade fade-left"></div>
      <div className="fade fade-right"></div>

      <div className="logos-track">
        {/* Duplicating logos to create infinite scroll */}
        {[...logos, ...logos].map((logo, index) => (
          <div key={index} className="logo-item">
            <img src={logo} alt={`Company logo ${index}`} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default TrustedCompanies;